mapper.select:
SELECT  *
FROM  (
	SELECT ROWNUM RNUM, B.*
	FROM(
		SELECT
			 /*+ INDEX_DESC(SAMPLE8 SAMPLE8_PK) */  ID, NAME, DESCRIPTION, USE_YN, REG_USER
		FROM SAMPLE8
		WHERE 1=1



					AND	ID LIKE '%' || #{searchKeyword} || '%'


					AND	NAME LIKE '%' || #{searchKeyword} || '%'



	) B WHERE ROWNUM <= #{firstIndex} + #{recordCountPerPage}
)
WHERE  RNUM  > #{firstIndex}
